
				
		function setCookie(param, val, exp) {
			var today = new Date();
			// today.setTime(today.getTime() + (exp * 24 * 60 * 60 * 1000));   // cookie for days
			today.setTime(today.getTime() + (exp * 2 * 60 * 1000));
			var expire = "expires=" + today.toUTCString();
			document.cookie = param + "=" + val + ';';
			//document.cookie = param + "=" + val + ';' + expire + ';path=/';
		}
		
		function getCookie(param) {
			var name = param + "=";
			var ca = document.cookie.split(';');
			for (var i = 0; i < ca.length; i++) {
				var c = ca[i];
				while(c.charAt(0) == ' ') {
					c = c.substring(1);
				}
				if (c.indexOf(name) == 0) {
					return c.substring(name.length, c.length);
				}
			}
			
			return "";
		}
		
		function deleteCookie(param) {
			document.cookie = param + "=;expires=Thu, 01 Jan 1970 00:00:01 GMT;";
		}